mapper.select:
SELECT m.id, m.material_code AS product_code, m.material_name AS product_name, mc.category_name AS category, m.type, m.style, m.specification, m.unit, m.price, m.expected_delivery_days, m.description, m.status, m.create_time, m.update_time FROM material m LEFT JOIN material_category mc ON m.category_id = mc.id
AND m.material_code LIKE CONCAT('%', #{productCode}, '%')
AND m.material_name LIKE CONCAT('%', #{productName}, '%')
AND mc.category_name = #{category}
AND m.status = #{status}
ORDER BY m.create_time DESC
