mapper.select:
SELECT po.*, u.real_name as creator_name, s.supplier_name, r.requirement_code FROM purchase_order po LEFT JOIN sys_user u ON po.creator_id = u.id LEFT JOIN supplier s ON po.supplier_id = s.id LEFT JOIN requirement r ON po.requirement_id = r.id WHERE 1=1
AND (po.order_code LIKE CONCAT('%', #{keyword}, '%') OR r.requirement_code LIKE CONCAT('%', #{keyword}, '%'))
AND po.status = #{status}
AND po.creator_id = #{creatorId}
ORDER BY po.create_time DESC LIMIT #{offset}, #{pageSize}

SELECT COUNT(1) FROM purchase_order po LEFT JOIN requirement r ON po.requirement_id = r.id WHERE 1=1
AND (po.order_code LIKE CONCAT('%', #{keyword}, '%') OR r.requirement_code LIKE CONCAT('%', #{keyword}, '%'))
AND po.status = #{status}
AND po.creator_id = #{creatorId}

mapper.update:
UPDATE purchase_order
order_code = #{orderCode}, requirement_id = #{requirementId}, supplier_id = #{supplierId}, total_amount = #{totalAmount}, order_date = #{orderDate}, delivery_date = #{deliveryDate}, status = #{status}, remark = #{remark},
WHERE id = #{id}
