mapper.update:
UPDATE requirement
requirement_code = #{requirementCode}, project_id = #{projectId}, scheme_id = #{schemeId}, creator_id = #{creatorId}, auditor_id = #{auditorId}, purpose = #{purpose}, priority = #{priority}, status = #{status}, expected_delivery_date = #{expectedDeliveryDate}, expected_delivery_days = #{expectedDeliveryDays}, deadline = #{deadline}, total_payment = #{totalPayment}, audit_time = #{auditTime}, audit_remark = #{auditRemark}, remark = #{remark},
WHERE id = #{id}

mapper.select:
SELECT r.*, p.project_name, s.scheme_name, u.real_name as creator_name, u.role as creator_role, au.real_name as auditor_name FROM requirement r LEFT JOIN project p ON r.project_id = p.id LEFT JOIN scheme s ON r.scheme_id = s.id LEFT JOIN sys_user u ON r.creator_id = u.id LEFT JOIN sys_user au ON r.auditor_id = au.id WHERE 1=1
AND (r.requirement_code LIKE CONCAT('%', #{keyword}, '%') OR p.project_name LIKE CONCAT('%', #{keyword}, '%') OR s.scheme_name LIKE CONCAT('%', #{keyword}, '%'))
AND r.status = #{status}
AND r.priority = #{priority}
AND r.creator_id = #{creatorId}
ORDER BY r.create_time DESC LIMIT #{offset}, #{pageSize}

SELECT COUNT(1) FROM requirement r LEFT JOIN project p ON r.project_id = p.id LEFT JOIN scheme s ON r.scheme_id = s.id WHERE 1=1
AND (r.requirement_code LIKE CONCAT('%', #{keyword}, '%') OR p.project_name LIKE CONCAT('%', #{keyword}, '%') OR s.scheme_name LIKE CONCAT('%', #{keyword}, '%'))
AND r.status = #{status}
AND r.priority = #{priority}
AND r.creator_id = #{creatorId}

SELECT COUNT(1)
FROM requirement
WHERE project_id = #{projectId}
AND purpose = #{purpose}

    AND id != #{excludeId}
