mapper.select:
SELECT s.id, s.scheme_code, s.scheme_name, s.project_id, s.version, s.description, s.creator_id, u.company AS creator_company, s.status, s.create_time, s.update_time FROM scheme s LEFT JOIN sys_user u ON s.creator_id = u.id
AND s.project_id = #{params.projectId}
AND s.status = #{params.status}
AND s.scheme_name LIKE CONCAT('%', #{params.schemeName}, '%')
ORDER BY s.create_time DESC

SELECT COUNT(*) FROM scheme
WHERE scheme_name = #{schemeName}
AND project_id = #{projectId}

    AND id != #{excludeId}
